package hnqd.aparmentmanager.common.Enum;import hnqd.aparmentmanager.common.exceptions.CommonException;import lombok.Getter;@Getterpublic enum ESecurityType {    CHECK_IN(0, "Check in"),    CHECK_OUT(1, "Check out"),    DENIED(1, "Denied"),    SUSPICIOUS(1, "Suspicious"),    FLAGGED(1, "Flagged"),;    private Integer code;    private String name;    ESecurityType(Integer code, String name) {        this.code = code;        this.name = name;    }    public static ESecurityType safeValueOfCode(Integer code) {        for (ESecurityType visitorStatus : ESecurityType.values()) {            if (visitorStatus.getCode() == code) {                return visitorStatus;            }        }        throw new CommonException.UnknownValuesException("Unknown enum with values: " + code);    }    public static ESecurityType safeValueOfName(String name) {        for (ESecurityType visitorStatus : ESecurityType.values()) {            if (visitorStatus.getName().equalsIgnoreCase(name)) {                return visitorStatus;            }        }        throw new CommonException.UnknownValuesException("Unknown enum with values: " + name);    }}