package hnqd.aparmentmanager.common.Enum;import hnqd.aparmentmanager.common.exceptions.CommonException;import lombok.Getter;@Getterpublic enum EVisitStatus {    PENDING(0, "Pending"), CONFIRMED(1, "Confirmed"), REJECTED(2, "Rejected");    private Integer code;    private String name;    EVisitStatus(Integer code, String name) {        this.code = code;        this.name = name;    }    public static EVisitStatus safeValueOfCode(Integer code) {        for (EVisitStatus visitorStatus : EVisitStatus.values()) {            if (visitorStatus.getCode() == code) {                return visitorStatus;            }        }        throw new CommonException.UnknownValuesException("Unknown enum with values: " + code);    }    public static EVisitStatus safeValueOfName(String name) {        for (EVisitStatus visitorStatus : EVisitStatus.values()) {            if (visitorStatus.getName().equalsIgnoreCase(name)) {                return visitorStatus;            }        }        throw new CommonException.UnknownValuesException("Unknown enum with values: " + name);    }}