package hnqd.aparmentmanager.visitorservice.dto.request;import jakarta.validation.constraints.NotBlank;import lombok.Getter;import lombok.Setter;import java.time.LocalDateTime;import java.util.List;@Getter@Setterpublic class VisitRequestDto {    @NotBlank(message = "Resident id is not allow to blank")    private Integer residentId;    @NotBlank(message = "Visit date is not allow to blank")    private LocalDateTime visitDate;    @NotBlank(message = "Expected checkin time is not allow to blank")    private LocalDateTime expectedCheckinTime;    @NotBlank(message = "Room id is not allow to blank")    private int roomId;    @NotBlank(message = "Visitors is not allow to blank")    private List<VisitorRequestDto> visitors;}