package hnqd.aparmentmanager.visitorservice.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import hnqd.aparmentmanager.common.Enum.ESecurityType;import hnqd.aparmentmanager.visitorservice.converter.SecurityTypeConverter;import jakarta.persistence.*;import java.time.LocalDateTime;import lombok.Getter;import lombok.Setter;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import org.springframework.data.annotation.LastModifiedDate;@Entity@Getter@Setterpublic class SecurityLog {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    @ManyToOne    @JoinColumn(name = "visitId", nullable = false)    @JsonIgnore    private Visitor visitor;    @ManyToOne    @JoinColumn(name = "visitRequestId", nullable = false)    @JsonIgnore    private VisitRequest visitRequest;    private LocalDateTime checkinTime;    private LocalDateTime checkoutTime;    private Integer checkinStaffId;    private Integer checkoutStaffId;    @Column(name = "status", nullable = false)    @Convert(converter = SecurityTypeConverter.class)    private ESecurityType status;    @Column(updatable = false)    @CreationTimestamp    private LocalDateTime createdAt;    @UpdateTimestamp    private LocalDateTime updatedAt;}