package hnqd.aparmentmanager.visitorservice.entity;import hnqd.aparmentmanager.common.Enum.ESecurityType;import hnqd.aparmentmanager.visitorservice.converter.SecurityTypeConverter;import jakarta.persistence.*;import java.time.LocalDateTime;import lombok.Getter;import lombok.Setter;import org.hibernate.annotations.CreationTimestamp;import org.springframework.data.annotation.LastModifiedDate;@Entity@Getter@Setterpublic class SecurityLog {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    @ManyToOne    @JoinColumn(name = "visitId", nullable = false)    private Visitor visitor;    @ManyToOne    @JoinColumn(name = "visitRequestId", nullable = false)    private VisitRequest visitRequest;    private LocalDateTime checkinTime;    private LocalDateTime checkoutTime;    private Integer checkinStaffId;    private Integer checkoutStaffId;    @Column(name = "status", nullable = false)    @Convert(converter = SecurityTypeConverter.class)    private ESecurityType status;    @Column(updatable = false)    @CreationTimestamp    private LocalDateTime createdAt;    @LastModifiedDate    private LocalDateTime updatedAt;}