package hnqd.aparmentmanager.visitorservice.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import java.util.ArrayList;import java.util.HashSet;import java.util.List;import lombok.Getter;import lombok.Setter;import org.hibernate.annotations.CreationTimestamp;import java.time.LocalDateTime;import java.util.Set;@Entity@Getter@Setterpublic class Visitor {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    @Column(nullable = false)    private String fullName;    @Column(nullable = false, length = 20)    private String phone;    @Column(unique = true, length = 50)    private String idCardNumber;    @JsonIgnore    @OneToMany(mappedBy = "visitor", cascade = CascadeType.ALL, orphanRemoval = true)    private Set<VisitRequestVisitor> visitRequestVisitors = new HashSet<>();    @JsonIgnore    @OneToMany(mappedBy = "visitor", cascade = CascadeType.ALL, orphanRemoval = true)    private Set<SecurityLog> securityLogs = new HashSet<>();    @Column(updatable = false)    @CreationTimestamp    private LocalDateTime createdAt;}